import React from 'react';


const Counter: React.FC = () => {
    const [count, setCount] = React.useState(0)
    const [color, setColor] = React.useState("white")

    const updateColor = () => {
        const ColorCode = 'rgb(' + (Math.floor(Math.random() * 256)) + ',' + (Math.floor(Math.random() * 256)) + ',' + (Math.floor(Math.random() * 256)) + ')';
        setColor(ColorCode)
    }

    React.useEffect(() => {
        setInterval(() => {
            setCount(counter => counter + 1)
            updateColor()
        },1)
      }, []);


    return (<div  style={Object.assign(styles.counter, {backgroundColor: color})}>
        <span style={styles.number}>{count}</span>
    </div>)
}

const styles = {
    counter: {
        flex: 1,
        justifyContent: 'center'
    },
    number: {
        fontSize: 16,
        textAlign: "center" as "center"
    }
}

export default Counter
